// @ts-check

/**
 * Adds two numbers
 * @param {number} a
 * @param {number} b
 * @returns {number}
 */
function add(a, b) {
    return a + b;
}

// Avoid disabling type checking or skipping types entirely
// @ts-nocheck

function greet(user) {
    return user.toUpperCase(); // Runtime error if user is not a string
}

// .eslintrc.js
module.exports = {
    parser: "@typescript-eslint/parser",
    extends: [
        "eslint:recommended",
        "plugin:@typescript-eslint/recommended",
        "prettier"
    ]
};

// Command-line usage
npx tsc --noEmit
npx ts-node src/index.ts

// Don't ignore type errors with 'any' or skip linting
const user: any = getUser(); // Type safety lost

// Not using a formatter leads to inconsistent style
function sayHello( name:string ){ console.log( name ) }

type ButtonProps = {
    label: string;
    onClick: () => void;
};

const Button: React.FC<ButtonProps> = ({ label, onClick }) => (
    <button onClick={onClick}>{label}</button>
);

// Vue 3 example (with script setup)
/*
<script setup lang="ts">
defineProps<{ msg: string }>();
</script>
*/

// Don't skip typing props
const Card = (props) => {
    return <div>{props.title}</div>; // No type safety
}

// Avoid using 'any' in API handlers
app.get("/user", (req: any, res: any) => {
    res.send("User");
});
